INSERT INTO words (word, description, parent_id) VALUES ('Веб-приложение', 'Программа, хранящаяся на удаленном сервере и запускаемая пользователями через веб-браузер.', 1);
INSERT INTO words (word, description, parent_id) VALUES ('API (Application programming interface)', 'Программный интерфейс приложений, набор инструкций, который позволяет разным приложениям общаться между собой.', 2);
INSERT INTO words (word, description, parent_id) VALUES ('Клиент-серверная архитектура', 'Распределенная структура приложения, которая распределяет задачи или рабочую нагрузку между поставщиками ресурсов или услуг, называемыми серверами, и запросчиками услуг, называемыми клиентами.', 2);
INSERT INTO words (word, description, parent_id) VALUES ('Веб-сервер', 'Компьютерное программное обеспечение и базовое оборудование, которое принимает запросы через HTTP (сетевой протокол, созданный для распространения веб-контента) или его безопасный вариант HTTPS.', 4);
INSERT INTO words (word, description, parent_id) VALUES ('Веб-разработка', 'Процесс создания веб-сайта или веб-приложения, основными этапами которого являются веб-дизайн, вёрстка страниц, программирование на стороне клиента и сервера, а также конфигурирование веб-сервера.', 2);
INSERT INTO words (word, description, parent_id) VALUES ('Шаблон проектирования', 'Повторяемая архитектурная конструкция, представляющая собой решение проблемы проектирования в рамках некоторого часто возникающего контекста.', 6);
INSERT INTO words (word, description, parent_id) VALUES ('Язык программирования', 'Формальный язык, предназначенный для записи компьютерных программ.', 6);
INSERT INTO words (word, description, parent_id) VALUES ('Python', 'Интерпретируемый язык программирования общего назначения высокого уровня с динамической типизацией, поддерживающий несколько парадигм программирования, включая структурированное (особенно процедурное), объектно-ориентированное и функциональное программирование.', 8);
INSERT INTO words (word, description, parent_id) VALUES ('Бессерверные вычисления', 'Модель выполнения облачных вычислений, в которой поставщик облачных услуг управляет сервером и выделяет машинные ресурсы по требованию. Когда приложение не используется, ему не выделяются вычислительные ресурсы.', 2);
INSERT INTO words (word, description, parent_id) VALUES ('Back-end как услуга (BaaS)', 'Модель, предполагающая вместо разработки и поддержки собственных сервисов использовать готовые решения, набор которых формирует универсальный back-end для любого проекта и предлагает разработчикам возможность связывать веб-приложения с серверным облачным хранилищем и API.', 10);
INSERT INTO words (word, description, parent_id) VALUES ('Бессерверные функции', 'Одноцелевые программные функции, размещаемые в управляемой инфраструктуре.', 10);
INSERT INTO words (word, description, parent_id) VALUES ('Функция как услуга (FaaS)', 'Модель, предполагающая декомпозицию серверной части веб-приложения на набор не фиксирующих состояние функций для обработки событий и запросов.', 10);
INSERT INTO words (word, description, parent_id) VALUES ('Yandex.Cloud', 'Российская платформа облачных сервисов от компании яндекс  предоставляющая пользователям инфраструктуру и вычислительные ресурсы в формате as a service.', 13);
INSERT INTO words (word, description, parent_id) VALUES ('Google Cloud Platform (GCP)', 'Набор сервисов облачных вычислений, который предоставляет ряд модульных облачных сервисов, включая вычисления, хранение данных, анализ данных и машинное обучение, а также набор инструментов управления.', 13);
INSERT INTO words (word, description, parent_id) VALUES ('Microsoft Azure', 'Платформа облачных вычислений, управляемая Microsoft, предлагающая доступ, управление и разработку приложений и услуг через глобальные центры обработки данных.', 13);
INSERT INTO words (word, description, parent_id) VALUES ('AWS', 'Платформа облачных вычислений, предоставляемая Amazon, которая включает в себя сочетание инфраструктуры как услуги (IaaS), платформы как услуги (PaaS) и пакетного программного обеспечения как услуги (SaaS)', 13);